package activity5;

public class activity5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum = 0;
		// Loop from 1 to 50
        for (int i = 1; i <= 50; i++) {
            sum += i; // Add each number to sum
        }

        // Print the result
        System.out.println("The sum of numbers from 1 to 50 is: " + sum);
	}

}

